import math

def is_sqare(x: int): 
    num = math.isqrt(x)

    if(pow(num, 2)==x):
        return True
    else:
        return False


def calculate_prime_numbers(intervall: int, x: int):
    i = math.isqrt(x)+1

    ctr = 0
    while (is_sqare(pow(i,2) - x) == False | ctr<intervall):
        i += 1
        ctr += 1

    print ("a= " + str(i))
    print ("b= " +str(math.sqrt((pow(i,2) - x))))

calculate_prime_numbers(1000, )
